CREATE TABLE "public"."nodes_rpc_addrs"("rpc_addr" varchar NOT NULL, "zone" varchar NOT NULL, "ip_or_dns" varchar NOT NULL, "node_id" varchar, "version" varchar, "moniker" varchar, "tx_index" varchar, "last_block_height" integer, "is_hidden" boolean NOT NULL DEFAULT false, "is_prioritized" boolean NOT NULL DEFAULT false, "is_alive" boolean NOT NULL DEFAULT false, "response_time" integer, "last_active" timestamp, "last_checked_at" timestamp NOT NULL DEFAULT now(), PRIMARY KEY ("rpc_addr") , FOREIGN KEY ("zone") REFERENCES "public"."zones"("chain_id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("ip_or_dns") REFERENCES "public"."nodes_addrs"("ip_or_dns") ON UPDATE restrict ON DELETE restrict);
